async function callAPI(options) {
  return new Promise((resolve, reject) => {
    frappe.call({
      ...options,
      callback: async function (response) {
        resolve(response?.message || response?.value)
      }
    });
  })
}

callAPI({
  method: 'rt_bij.api.get_state_list',
  freeze: true,
  args: {},
  freeze_message: __("Getting States"),
}).then(state_list => {
  let stateSelectElement = root_element.getElementById('state_id');
  state_list.forEach(state => {
    stateSelectElement.innerHTML += `<option value="${state.name}">${state.state_name}</option>`;
  });
});

callAPI({
  method: 'rt_bij.api.get_option_list',
  freeze: true,
  args: {fields: ["name","option_type","option_name"]},
  freeze_message: __("Getting Options"),
}).then(option_listes => {
  const outputFiltere = option_listes.filter(option_list => option_list.option_type === 'Output' || option_list.option_type === 'Output IVCD' || option_list.option_type === 'Output ICVD');
  const activityFiltere = option_listes.filter(option_list => option_list.option_type === 'Activity ICVD' || option_list.option_type === 'Activity');
  let select_outpot = root_element.getElementById('output_id');
  let select_activity = root_element.getElementById('activity_id');
  outputFiltere.forEach(option_list => {
    select_outpot.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
  });
  activityFiltere.forEach(option_list => {
    select_activity.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
  });
});

