script:
function callAPI(options) {
    return new Promise((resolve, reject) => {
        frappe.call({
            ...options,
            callback: async function (response) {
                resolve(response?.message || response?.value);
            },
            error: function (error) {
                reject(error);
            }
        });
    });
}

async function fetchCR4SDOutput() {
    const endpoints = [
        { method: 'rt_bij.api.get_crs4d_output_count', selectors: ['#cr4sd_output_res', '#cr4sd_output_appr', '#cr4sd_output_rej', '#cr4sd_output_dpm', '#cr4sd_output_spm'] },
        { method: 'rt_bij.api.get_crs4d_activity_count', selectors: ['#crs4d_activity_res', '#crs4d_activity_appr', '#crs4d_activity_rej', '#crs4d_activity_dpm', '#crs4d_activity_spm'] },
        { method: 'rt_bij.api.get_ivcd_activity_count', selectors: ['#ivcd_activity_res', '#ivcd_activity_appr', '#ivcd_activity_rej', '#ivcd_activity_dpm', '#ivcd_activity_spm'] },
        { method: 'rt_bij.api.get_ivcd_output_count', selectors: ['#ivcd_output_res', '#ivcd_output_appr', '#ivcd_output_rej', '#ivcd_output_dpm', '#ivcd_output_spm'] }
    ];

    try {
        const results = await Promise.all(endpoints.map(endpoint => callAPI({
            method: endpoint.method,
            freeze: true,
            freeze_message: __("Sending message..."),
        })));
   console.log(results)
        results.forEach((data, index) => {
            if (data) {
                const selectors = endpoints[index].selectors;
                root_element.querySelector(selectors[0]).innerText = data.total_count || 0;
                root_element.querySelector(selectors[1]).innerText = data.approved_count || 0;
                root_element.querySelector(selectors[2]).innerText = data.rejected_count || 0;
                root_element.querySelector(selectors[3]).innerText = data.pending_dpm_count || 0;
                root_element.querySelector(selectors[4]).innerText = data.pending_spm_count || 0;
            }
        });
    } catch (error) {
        console.error('Error fetching data:', error);
        // Optionally update the UI to reflect the error state
        // root_element.querySelector('#error_message').innerText = 'Failed to fetch data. Please try again later.';
    }
}

fetchCR4SDOutput();
