//  Get State List
callAPI({
  method: 'rt_bij.api.common_api',
  freeze: true,
  args: {
      doctype_name: "State",
      fields: ["name","state_name"]
  },
  freeze_message: __("Getting States"),
}).then(state_list => {
  let stateSelectElement = root_element.getElementById('state_id');
  state_list.forEach(state => {
    stateSelectElement.innerHTML += `<option value="${state.name}">${state.state_name}</option>`;
  });
});


var selectElement = root_element.getElementById('activity_select');
var select_output = root_element.getElementById('output_id');
let thrust_form = function(selectedValue) {
    callAPI({
        method: 'rt_bij.api.common_api',
        freeze: true,
        args: {
            doctype_name: "Options",
            fields: ["name", "option_type", "option_name", "output"]
        },
        freeze_message: __("Getting Options"),
    }).then(options_lists => {
        let select_output = root_element.getElementById('output_id');
            select_output.innerHTML = "<option value='' disabled selected>Output</option>";
        let select_activity = root_element.getElementById('activity_id');
            select_activity.innerHTML = "<option value='' disabled selected>Activity</option>";
        if (selectedValue === "Activity CR4SD" || selectedValue === "CR4SD Output Form") {
            const outputFiltered = options_lists.filter(option_list => option_list.option_type === 'Output');
            outputFiltered.forEach(option_list => {
                select_output.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
            });
        } else if (selectedValue === "Activity   ICVD") {
            const outputFiltered = options_lists.filter(option_list => option_list.option_type === 'Output ICVD');
            outputFiltered.forEach(option_list => {
                select_output.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
            });
        } else if (selectedValue === "IVCD Output Form") {
            const outputFiltered = options_lists.filter(option_list => option_list.option_type === 'Output IVCD');
            outputFiltered.forEach(option_list => {
                select_output.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
            });
        }
    }).catch(error => {
        console.error('Error fetching options:', error);
    });
};
selectElement.addEventListener('change', function() {
    var selectedOption = selectElement.options[selectElement.selectedIndex];
    var selectedValue = selectedOption.value;
    thrust_form(selectedValue);
});
let output = function(selectedValue) {
    callAPI({
        method: 'rt_bij.api.common_api',
        freeze: true,
        args: {
            doctype_name: "Options",
            fields: ["name", "option_type", "option_name", "output"]
        },
        freeze_message: __("Getting Options"),
    }).then(options_lists => {
        let select_output = root_element.getElementById('activity_id');
        select_output.innerHTML =  "<option value='' disabled selected>Activity</option>";
        const activityFiltered = options_lists.filter(option_list => 
            (option_list.option_type === 'Activity' || option_list.option_type === 'Activity ICVD') && 
            option_list.output == selectedValue
        );
        activityFiltered.forEach(option_list => {
            select_output.innerHTML += `<option value="${option_list.name}">${option_list.option_name}</option>`;
        });
    }).catch(error => {
        console.error('Error fetching options:', error);
    });
};
select_output.addEventListener('change', function() {
    var selectedOutput = select_output.options[select_output.selectedIndex];
    var selectedOutputValue = selectedOutput.value;
    output(selectedOutputValue);
});

